# you can simply add the profiles to each other to get combinations of them. For example:
full = low + medium + high + extra_high

low_distance = 3.09
medium_distance = 4.76
high_distance = 7.16
extra_high_distance = 8.25
full_distance = low_distance + medium_distance + high_distance + extra_high_distance

# assumption: 96s60p battery pack = 96 cells in series, 60 in parallel -> ca. 63360 Wh
P_EV_KW_TO_P_CELL_W = 1000.0 / (96.0 * 60.0)  # p_cell_W = p_ev_kW * P_EV_KW_TO_P_CELL_W
P_CELL_W_TO_P_EV_KW = 1.0 / P_EV_KW_TO_P_CELL_W  # p_ev_kW = p_cell_W * P_CELL_W_TO_P_EV_KW
bat_capacity_kWh = 64  # the battery capacity of the EV for which the driving profile was calculated
